public onInit(): Promise&lt;any&gt; {
        return new Promise&lt;any&gt;((resolve: (value?: boolean | any) =&gt; void, reject: (reason?: any) =&gt; void) =&gt; {
       this.getCurrentPosition().subscribe((response: any) =&gt; {
