	    }else{ //Otherwise Deselect all items.
	         $(&quot;.js-select2&quot;).val(null).trigger(&#39;change&#39;);
	     }
	});
	
	$(&quot;#checkbox&quot;).click();  //Manually click the checkbox so that it is selected 1st time.
});
````
