    static void rotate(int arr[], int k) {
    	for (int i = 1; i &lt;= k; i++) {
    		move(arr);
    	}
    }
    
    static void move(int arr[]) {
    	int i, n, tmp;
    	n = arr.length;
    	tmp = arr[0];
    	for (i = 0; i &lt; n - 1; i++) {
    		arr[i] = arr[i + 1];
    	}
    	arr[i] = tmp;
    }
    
    public static void main(String[] args) {
