          $(&quot;.b1&quot;).fadeIn(100);
        }

        if ($(&quot;.sample:last&quot;).hasClass(&quot;img-active&quot;)) {
          $(&quot;.b2&quot;).fadeOut(100);
        } else {
          $(&quot;.b2&quot;).fadeIn(100);
        }
      }

      checkIimg();

      $(&quot;.buttons button&quot;).click(function() {
        if ($(this).hasClass(&quot;b2&quot;)) {
          $(&quot;.sample.img-active&quot;).addClass(&quot;img-hidden&quot;).fadeOut(100, function() {
            $(this).removeClass(&quot;img-active&quot;).next(&quot;.img-hidden&quot;).addClass(&quot;img-active&quot;).fadeIn(100);
            checkIimg();
