    		var = 1;
    		ssleep(4);
    		write_unlock(&amp;rwlock);
    		printk(&quot;w: write unlock\n&quot;);
    		if (kthread_should_stop()) {
    			printk(&quot;w: should stop now\n&quot;);
    			return 1;
    		} else {
    			printk(&quot;w: continue...\n&quot;);
    		}
    	}
