    `;

    function App() {
      const [cryptos, setCryptos] = React.useState(null);
      React.useEffect(() =&gt; {
        fetch(&quot;https://api.coincap.io/v2/assets&quot;)
          .then(res =&gt; {
            return res.json();
          })
          .then(res =&gt; {
