unit Unit2;

interface

type
  Gazi = class
  private
    { private declarations }
  public
    RektorAdi : String;
    { public declarations }
  end;

var
  GlobalGazi: Gazi;

implementation

initialization
  GlobalGazi := Gazi.Create;
finalization
  GlobalGazi.Free;

end.
```
