to 

    xhr.onload = function() {
        if (xhr.status != 200) { // analyze HTTP status of the response
            alert(`Error ${xhr.status}: ${xhr.statusText}`); // e.g. 404: Not Found
    
        } else {
            var blob = new File([this.response], {type: &#39;image/png&#39;}); 
            // You wanted to add to the array.
