typedef std::pair&lt;std::string,std::string&gt; pair;

struct pair_hash
{
	template &lt;class T1, class T2&gt;
	std::size_t operator() (const std::pair&lt;T1, T2&gt; &amp;pair) const
	{
		return std::hash&lt;T1&gt;()(pair.first) ^ std::hash&lt;T2&gt;()(pair.second);
