                StartDate = new DateTime(2018, 11, 01),
                EndDate = new DateTime(2019, 01, 08),
                Amount = 20000
            }
        };

        var amountsPerMonth = lstAmountPerPeriod.SelectMany(AmountPerMonth.FromPeriod);

        Console.WriteLine(&quot;ID |month |year   |amount&quot;);
