    const buckets = [1, 2, 2.5, 5, 10, 25, 50, 100, 200, 250, 500, 1000];
    
    const findPriceIntervalPerGridSquare = ({ minPrice, maxPrice, nrSquares }) =&gt; {
      const priceRange = maxPrice - minPrice;
    
      let idealBucket;
      for(let bucket of buckets) {
        if ((priceRange / nrSquares) &lt; bucket) {
