    const lookupTable = Array(10).fill(0)
      .map((_, i) =&gt; Array(10).fill(0)
        .map((_, j) =&gt; [`${i},${j}`, i+j+&#39;&#39;]))
      .reduce((flatArr, innerArr) =&gt; [...flatArr, ...innerArr], [])
      .reduce((lookupTable, [k, v]) =&gt; ({...lookupTable, [k]: v}), {})

