                JsonSerializer serializer = new JsonSerializer();
                ObjectDetails objectDetails = serializer.Deserialize&lt;ObjectDetails&gt;(
                    jsonTextReader);
                return objectDetails;
            }
        }
        catch (Exception e)
        {
            // Log exception
            return null;
        }
