package test;

import java.util.concurrent.locks.ReentrantReadWriteLock;
import java.util.concurrent.locks.ReentrantReadWriteLock.ReadLock;
import java.util.concurrent.locks.ReentrantReadWriteLock.WriteLock;

public class App implements AutoCloseable {
	private final ReentrantReadWriteLock lock = new ReentrantReadWriteLock();
	private final ReadLock readLock = lock.readLock();
	private final WriteLock writeLock = lock.writeLock();

	private volatile long ptr;
	private volatile long ptrRef;
	private volatile boolean reload;

	static {
		System.loadLibrary(&quot;foo&quot;);
	}

	public static void main(String[] args) throws InterruptedException {
		try (App app = new App()) {
			for (int i = 0; i &lt; 20; i++) {
				new Thread(() -&gt; {
					while (true) {
						app.tryReload();
					}
				}).start();
			}

			while (true) {
				app.setReload();
			}
		}
	}

	public App() {
		this.ptr = this.create();
	}

	public void setReload() {
		writeLock.lock();
		try {
			reload = true;
		} finally {
			writeLock.unlock();
		}
	}

	public void tryReload() {
		readLock.lock();
		debug(&quot;Got read lock&quot;);

		if (reload) {
			debug(&quot;Cache is expired&quot;);

			readLock.unlock();
			debug(&quot;Released read lock coz expired&quot;);

			writeLock.lock();
			debug(&quot;Got write lock&quot;);

			try {
				if (reload) {
					fullReload();
				}

				readLock.lock();
				debug(&quot;Got read lock inside write&quot;);
			} finally {
				writeLock.unlock();
				debug(&quot;Released write lock&quot;);
			}
		}

		readLock.unlock();
		debug(&quot;Released read lock&quot;);
	}

	private void fullReload() {
		destroyRef();
		debug(&quot;Dropped ref&quot;);

		debug(&quot;Reloading&quot;);
		reload();
		debug(&quot;Reloading completed&quot;);

		updateRef();
		debug(&quot;Created ref&quot;);
		reload = false;
	}

	private void updateRef() {
		this.ptrRef = this.createRef();
	}

	private native void reload();

	private native long create();

	private native long createRef();

	private native void destroy();

	private native void destroyRef();

	@Override
	public void close() {
		writeLock.lock();
		try {
			this.destroy();
			this.ptrRef = 0;
			this.ptr = 0;
		} finally {
			writeLock.unlock();
		}
	}

	private static void debug(String s) {
		System.out.printf(&quot;%10s : %s%n&quot;, Thread.currentThread().getName(), s);
	}

}
