    def mergeByKey[K](list: List[(K, String)]): List[String] = {
      @annotation.tailrec
      def loop(remaining: List[(K, String)], acc: Map[K, StringBuilder]): List[String] =
        remaining match {
          case Nil =&gt;
            acc.valuesIterator.map(_.result()).toList
          
          case (key, value) :: tail =&gt;
            loop(
