    function search(data, searchKey) {
      return Object.entries(data).filter(([key, value]) =&gt; {
        return key.toLowerCase().includes(searchKey.toLowerCase()) ||
          value.toLowerCase().includes(searchKey.toLowerCase());
      });
    }

