      if(event-&gt;key() == Qt::Key_Return || event-&gt;key() == Qt::Key_Enter)
      {
        if(lineEdit-&gt;text().isEmpty())
        {
          lineEdit-&gt;completer()-&gt;setCompletionPrefix(&quot;&quot;);
          lineEdit-&gt;completer()-&gt;complete();
        }
      }

