    public authenticateUser(user: User, res: Response) {
    		const authJwtToken = this.generateJWT({
    			email: user.email,
    			uuid: user.uuid
    		});
    
    		const cookieOptions = {
    			maxAge: 3600000,
    			secure: true,
    			httpOnly: true
    		};
    
    		res.cookie(&#39;access_token&#39;, authJwtToken, cookieOptions);
