    extension Date {
        var hour: Int { Calendar.current.component(.hour, from: self) }
        var minute: Int { Calendar.current.component(.minute, from: self) }
        var previousHourTenth: Date { Calendar.current.date(bySettingHour: hour, minute: minute - minute % 10, second: 0, of: self)! }
        func lastNthHourTenth(n: Int) -&gt; [Date] { (0..&lt;n).map {  Calendar.current.date(byAdding: .minute, value: -10*$0, to: previousHourTenth)! } }
