            if (this.selected.length &lt; this.maxSelect)
              return true;
            return false;
          },
          onChange(item) {
            if (this.isSelected(item)) {
              this.selected = this.selected.filter(val =&gt; val !== item);
            } else if (this.isValidSelection()) {
              this.selected.push(item);
            }
          },
        },
      };
