        public static Specification&lt;User&gt; UsersNotInSelectedOperations(final List&lt;Long&gt; operationIds) {
        		return (root, query, builder) -&gt; {
        				Subquery&lt;Operation&gt; subquery = query.subquery(Operation.class);
