    (
        [0] =&gt; Array
            (
                [0] =&gt; jan
                [1] =&gt; feb
                [2] =&gt; mar
            )    
        [1] =&gt; Array
            (
                [0] =&gt; may
                [1] =&gt; june
            )    
        [2] =&gt; Array
            (
                [0] =&gt; dec
            )    
    )

[Demo on 3v4l.org][1]


