    function squish(items) {
      const squishedItemsByName = items.reduce((a, { name, ...props }) =&gt; {
        if (!a[name]) {
          a[name] = { name };
        }
        Object.entries(props).forEach(([prop, val]) =&gt; {
          a[name][prop] = (a[name][prop] || 0) + val;
        });
        return a;
      }, {});
      return Object.values(squishedItemsByName);
    }

    console.log(squish(items));

