	  }
	}

	class MyHomePage extends StatefulWidget {
	  MyHomePage({Key key, this.title}) : super(key: key);

	  // This widget is the home page of your application. It is stateful, meaning
	  // that it has a State object (defined below) that contains fields that affect
	  // how it looks.

	  // This class is the configuration for the state. It holds the values (in this
	  // case the title) provided by the parent (in this case the App widget) and
	  // used by the build method of the State. Fields in a Widget subclass are
	  // always marked &quot;final&quot;.

	  final String title;

	  @override
	  _MyHomePageState createState() =&gt; _MyHomePageState();
	}

	class _MyHomePageState extends State&lt;MyHomePage&gt; {
	  List&lt;Item&gt; items;
	  bool _loadingMore;
	  bool _hasMoreItems;
	  int _maxItems = 30;
	  int _numItemsPage = 10;
	  Future _initialLoad;

	  Future _loadMoreItems() async {
		final totalItems = items.length;
		await Future.delayed(Duration(seconds: 3), () {
		  for (var i = 0; i &lt; _numItemsPage; i++) {
			items.add(Item(&#39;Item ${totalItems + i + 1}&#39;));
		  }
		});

		_hasMoreItems = items.length &lt; _maxItems;
	  }

	  @override
	  void initState() {
		super.initState();
		_initialLoad = Future.delayed(Duration(seconds: 3), () {
		  items = List&lt;Item&gt;();
		  for (var i = 0; i &lt; _numItemsPage; i++) {
			items.add(Item(&#39;Item ${i + 1}&#39;));
		  }
		  _hasMoreItems = true;
		});
	  }

	  @override
	  Widget build(BuildContext context) {
		return Scaffold(
		  backgroundColor: Colors.yellow,
		  appBar: AppBar(
			title: Text(widget.title),
		  ),
		  body: FutureBuilder(
			  future: _initialLoad,
			  builder: (context, snapshot) {
				switch (snapshot.connectionState) {
				  case ConnectionState.waiting:
					return Center(child: CircularProgressIndicator());
				  case ConnectionState.done:
					return IncrementallyLoadingListView(
					  hasMore: () =&gt; _hasMoreItems,
					  itemCount: () =&gt; items.length,
					  loadMore: () async {
						// can shorten to &quot;loadMore: _loadMoreItems&quot; but this syntax is used to demonstrate that
						// functions with parameters can also be invoked if needed
						await _loadMoreItems();
					  },
					  onLoadMore: () {
						setState(() {
						  _loadingMore = true;
						});
					  },
					  onLoadMoreFinished: () {
						setState(() {
						  _loadingMore = false;
						});
					  },
					  loadMoreOffsetFromBottom: 2,
					  itemBuilder: (context, index) {
						final item = items[index];
						if ((_loadingMore ?? false) &amp;&amp; index == items.length - 1) {
						  return Column(
							children: &lt;Widget&gt;[
							  ItemCard(item: item),
							  Card(
								child: Padding(
								  padding: const EdgeInsets.all(16.0),
								  child: Column(
									children: &lt;Widget&gt;[
									  Row(
										crossAxisAlignment:
										CrossAxisAlignment.start,
										children: &lt;Widget&gt;[
										  Container(
											width: 60.0,
											height: 60.0,
											color: Colors.grey,
										  ),
										  Padding(
											padding: const EdgeInsets.fromLTRB(
												8.0, 0.0, 0.0, 0.0),
											child: Container(
											  color: Colors.grey,
											  child: Text(
												item.name,
												style: TextStyle(
													color: Colors.transparent),
											  ),
											),
										  )
										],
									  ),
									  Padding(
										padding: const EdgeInsets.fromLTRB(
											0.0, 8.0, 0.0, 0.0),
										child: Container(
										  color: Colors.grey,
										  child: Text(
											item.message,
											style: TextStyle(
												color: Colors.transparent),
										  ),
										),
									  )
									],
								  ),
								),
							  ),
							],
						  );
						}
						return ItemCard(item: item);
					  },
					);
				  default:
					return Text(&#39;Something went wrong&#39;);
				}
			  }),
		);
	  }
	}

	class ItemCard extends StatelessWidget {
	  const ItemCard({
		Key key,
		@required this.item,
	  }) : super(key: key);

	  final Item item;

	  @override
	  Widget build(BuildContext context) {
		return GestureDetector(
		  child: Card(
			child: Padding(
			  padding: const EdgeInsets.all(16.0),
			  child: Column(
				children: &lt;Widget&gt;[
