    scanf(&quot;%d&quot;, &amp;num);
    m = num / 1000;
    d = (num % 1000) / 500;
    c = (num % 500) / 100;
    l = (num % 100) / 50;
    x = (num % 50) / 10;
    v = (num % 10) / 5;
    i = (num % 5);
    for (m; m &gt; 0; m--)
    {
        printf(&quot;M&quot;);
    }
    if (num%1000&gt;=900)
    {
        printf(&quot;CM&quot;);
        d = d - 1;
        c = c - 4;
        num = num - 900;
    }
