  let getFullNameSpy;
  let alertSpy;
  beforeEach(() =&gt; {
    alertSpy = jest.spyOn(window, &#39;alert&#39;);
    getFullNameSpy = jest.spyOn(SomeComponent.prototype as any, &#39;getFullName&#39;);
    wrapper = shallow(&lt;SomeComponent&gt;&lt;/SomeComponent&gt;);
