    $sth-&gt;bindParam(&#39;:username&#39;, $request-&gt;Info-&gt;Name, PDO::PARAM_STR);
    $sth-&gt;bindParam(&#39;:date&#39;, $request-&gt;Info-&gt;Date);
    $sth-&gt;bindParam(&#39;:id&#39;, $id);
    $sth-&gt;execute();

