```
let newArray = [];

//create a function that goes through the reference array (the array you want to create) 
//and let&#39;s you know if id doesn&#39;t already exist, or the index of the object in the 
//reference array if the id exists

function checkIfidExists(id,refArr){ 
  let found = false;
  for(let i=0; i&lt;refArr.length; i++){
    if (refArr[i].id == id){
      found = i; 
      break;
    }
  }
  return found;
}

//create a function that receives the array you want to add as a new object
function newArrayAddition(array, refArray){
  array.map(obj =&gt;{
  //store the index of the object with the id or store falsy value if none is found
  let index = checkIfidExists(obj.id, refArray)
  if(!index){
    refArray.push({
      id: obj.id,
      name: obj.name,
      comments: [
        {
          id : 1,
          comment: obj.comment
        }
      ]
    })
  }else{
    refArray[index].comments.push({
      id: refArray[index].comments.length + 1,
      comment: obj.comment
    })
  }
})
}


newArrayAddition(id1, newArray);
newArrayAddition(id2, newArray);
newArrayAddition(id3, newArray);

console.log(newArray);
```

