    function fixKeys(obj) {
      if (Array.isArray(obj)) {
        obj.forEach(item =&gt; fixKeys(item));
      } else {
        Object.keys(obj).forEach(key =&gt; {
          if (key.match(/\s+/g)) {
            let newKey = key.replace(/\s+/g, &#39;&#39;);
            obj[newKey] = obj[key];
            delete obj[key];
          }
        });
      }
      return obj;
    }

