    from ((select levelname as name, levelid, 1 as ord
           from tablea
          ) union all
          (select &#39;  &#39; + username, levelid, 2 as ord
           from tableb
          ) union all
          (select null, levelid, 0 as ord
           from tablea
           where levelid &gt; 1
          )
         ) ul
    order by levelid, ord;

