        &lt;li 
           [style.background]=&quot;p?.status === &#39;Offline&#39;? &#39;grey&#39; : &#39;green&#39;&quot;
           class=&quot;li1&quot; 
           *ngFor=&quot;let p of myData?.paymentChannels&quot;&gt;
               &lt;span&gt;{{p.name}}&lt;/span&gt;
               &lt;br&gt;
               &lt;span *ngIf=&quot;p?.status === &#39;Offline&#39;; else empty&quot;&gt;{{p.status}}&lt;/span&gt;
               &lt;ng-template #empty&gt;$nbsp;&lt;/ng-template&gt;
         &lt;/li&gt;
    &lt;/ul&gt;


