    function happyLoop() {
      var spreadsheet = SpreadsheetApp.getActive();
      var sheet = spreadsheet.getSheets()[0];
      var C5 = sheet.getRange(&#39;C5&#39;);
      var E15 = sheet.getRange(&#39;E15&#39;).getValue();
