#include &lt;bits/stdc++.h&gt;
using namespace std;

int binomialCoeff(int n, int k) 
{ 
    vector&lt;int&gt; C(k+1,0); 
  
    C[0] = 1;  // nC0 is 1 
  
    for (int i = 1; i &lt;= n; i++) 
    { 
        // Compute next row of pascal triangle using 
        // the previous row 
        for (int j = min(i, k); j &gt; 0; j--) 
            C[j] = C[j] + C[j-1]; 
    } 
    return C[k]; 
}

