	class UnicornAlertDialog extends StatelessWidget {
	  const UnicornAlertDialog({
		Key key,
		@required this.gradient,
		this.title,
		this.titlePadding,
		this.titleTextStyle,
		this.content,
		this.contentPadding = const EdgeInsets.fromLTRB(24.0, 20.0, 24.0, 24.0),
