boolean isBalanced(TreeNode root) {
  if (root == null) { return true; }

  int heightDiff = getHeight(root.left) - getHeight(root.right);
  if (Maths.abs(heightDiff) &gt; 1) {
    return false;
  } else {
    return isBalanced(root.left) &amp;&amp; isBalanced(root.right);
