    &#160; &#160; &#160; &#160; &#160; &#160; // dynamic feature APK.
    &#160; &#160; &#160; &#160; &#160; &#160; enableSplit = false
    &#160; &#160; &#160; &#160; }
    &#160; &#160; &#160; &#160; density {
    &#160; &#160; &#160; &#160; &#160; &#160; // This property is set to true by default.
