     void RegisterAllIncludedInterfaces(string assemblyName, string @namespace)
        {
            System.Reflection.Assembly
                .Load(assemblyName)
                .GetTypes()
                .Where(t =&gt; t.GetInterfaces().Any(i =&gt; i.Namespace == @namespace))
                .ToList()
                .ForEach(t =&gt;
