      strDate  VARCHAR2(200);
    BEGIN
      strDate := TO_CHAR(NVL(:NEW.DATE_AND_TIME, :OLD.DATE_AND_TIME), &#39;DDMONRR&#39;);
    
      IF INSERTING THEN
        strStmt := &#39;INSERT INTO events_recorded&#39; || strDate ||
                   &#39;(txn, date_and_time, stack_trace, originating_file,originating_line_nr)&#39; ||
