               :NEW.originating_file, :NEW.originating_line_nr;
      ELSIF UPDATING THEN
        strStmt := &#39;UPDATE events_recorded&#39; || strDate ||
                   &#39; SET txn = :a, date_and_time = :b, stack_trace = :c, &#39; ||
                   &#39;     originating_file = :d, originating_line_nr = :e &#39; ||
                   &#39; WHERE txn = :f&#39;;
    
