    var init = function () {
        //return scroll position in session storage
        var scrollPages = JSON.parse(sessionStorage.scrollPages);
        scrollPages = typeof scrollPages === &quot;object&quot; ? scrollPages : {};
        $(window).scrollTop(scrollPages[currentPage] || 0)
    };
    window.onload = init;

