    const Ants = ({ rect }) =&gt; {
    
      if (!rect) {
        console.log(&quot;No ants&quot;)
        return &quot;&quot;
      }
    
      const svg = (
        &lt;svg
          width=&quot;100vw&quot;
          height=&quot;100vh&quot;
          xmlns=&quot;http://www.w3.org/2000/svg&quot;
