        &lt;/svg&gt;
      )
    
      return svg
    }
    
            
    
    class App extends React.Component {
      constructor() {
        super()
        this.state = { rect: null, selected: [], removed: [] }
        this.startDrag = this.startDrag.bind(this)
      }
    
    
      startDrag(event) {
        const startX = event.pageX
        const startY = event.pageY
    
        const drag = event =&gt; {
          const x      = Math.min(startX, event.pageX)
          const right  = Math.max(startX, event.pageX)
          const width  = right - x
          const y      = Math.min(startY, event.pageY)
          const bottom = Math.max(startY, event.pageY)
          const height = bottom - y
    
          const rect = { x, y, right, bottom, width, height }
          const selected = this._getSelectedSpanIds(rect)
    
          this.setState({ rect, selected })
        }
    
        const stopDrag = () =&gt; {
          document.documentElement.removeEventListener(&quot;mousemove&quot;, drag, false)
          document.documentElement.removeEventListener(&quot;mouseup&quot;, stopDrag, false)       
         
          const removed = this.state.removed.concat(this.state.selected)
          this.setState({ rect: null, selected: [], removed })
        }
    
        document.documentElement.addEventListener(&quot;mousemove&quot;, drag, false)
        document.documentElement.addEventListener(&quot;mouseup&quot;, stopDrag, false)
        drag(event) // treat case of a click with no mouse movement
      }
    
    
      _getSelectedSpanIds(rect) {
        const intersect = (rect1, rect2) =&gt; {
          return rect1.x &lt; rect2.right
              &amp;&amp; rect2.x &lt; rect1.right
              &amp;&amp;&#160;rect1.y &lt; rect2.bottom
              &amp;&amp; rect2.y &lt; rect1.bottom
        }
    
        return [].slice.call(document.querySelectorAll(&quot;span:not(.removed)&quot;))
                       .filter(span =&gt; (
                         intersect(span.getBoundingClientRect(), rect)
                       ))
                       .map(span =&gt; span.id)
      }
    
    
      render() {
        const data = [[&quot;⨷&quot;], [&quot;◯&quot;, &quot;&#128191;&quot;], [&quot;⚫&quot;, &quot;&#128308;&quot;, &quot;&#128309;&quot;]]
    
        return (
          &lt;div className=&quot;App&quot;&gt;
            &lt;Board
              // Fixed
              data={data}
              drag={this.startDrag}
              // Variable
              selected={this.state.selected}
              removed={this.state.removed}
            /&gt;
            &lt;Ants
              rect={this.state.rect}
            /&gt;
          &lt;/div&gt;
        )
      }
    }
    
    
    ReactDOM.render(&lt;App /&gt;, document.getElementById(&#39;app&#39;));

