    const validateUserSession = async (userSessionToken, userEmailId) =&gt; {
        var params = {
    	    ...
    	}
    	let data;
    	try {
    		data = await dynamodb.getItem(params).promise();
    	} catch (err) {
    		callback(null, generateDeny(&#39;user&#39;, event.methodArn));
