        std::cout &lt;&lt; &quot;int&quot;;
    }

main.cpp

    #include &quot;fnc.h&quot;
    
    extern template void fnc&lt;int&gt;(); // compiler will not generate fnc&lt;int&gt; and linker will be used to find &quot;fnc&lt;int&gt;&quot; in other object files
    
