            string htmlCode = client.DownloadString(&quot;http://dir.com/page.html&quot;);

            File.WriteAllText(@&quot;C:\localfile.html&quot;, htmlCode);
        }

        Thread.Sleep(60_000); // sleep for 60 seconds.
    }

