        #include &lt;map&gt;
        #include &lt;any&gt;
    
        int main() {
            auto elements = std::initializer_list&lt;std::pair&lt;const std::any, std::any&gt;&gt;{};
            auto mymap = std::map(elements, [](const std::any&amp; lhs, const std::any&amp; rhs){return false;});
