	//green candle
	if(dClose &gt; dOPen){
		dRTail = dOPen - dLow;
		dTail = dHigh - dClose;
		//now check ratios 
		if((dTail / dRange) &lt;= (NRTail_To_Range_Ratio / 100) &amp;&amp; (dBody / dRange) &lt;= (Body_To_Range_Ratio / 100) &amp;&amp; (dRTail / dRange) &gt;= (RTail_To_Range_Ratio / 100)){
			return 1;
		}
	}
	//red candle
	else{
		dRTail = dHigh - dOpen;
		dTail = dClose - dLow;
		//now check ratios
		if((dTail / dRange) &lt;= (NRTail_To_Range_Ratio / 100) &amp;&amp; (dBody / dRange) &lt;= (Body_To_Range_Ratio / 100) &amp;&amp; (dRTail / dRange) &gt;= (RTail_To_Range_Ratio / 100)){
			return -1;
		}
	}
	
	return 0;
}
