    my @nodes = $tree-&gt;look_down(_tag =&gt; &#39;img&#39;);
    
    for my $node (@nodes) {
        my ($ext) =  ( $node-&gt;attr(&#39;src&#39;) ) =~ m{.*/.*\.(.*)\z};    #/
