        assert np.all(nv[0:2] == a)
        assert np.all(nv[:2] == a)
        assert np.all(nv[-1] == b[-1])
        assert np.all(nv[:-1] == ab[:-1])
        assert np.all(nv[::-1] == ab[::-1])
    
    
