Here is how to implement an infinite scroll using this API:

    import React, { Component } from &quot;react&quot;;
    import ReactDOM from &quot;react-dom&quot;;
    import axios from &quot;axios&quot;;
    import InfiniteScroll from &quot;react-infinite-scroller&quot;;
    
    export default class App extends Component {
      state = {
        url: &quot;https://pokeapi.co/api/v2/pokemon/?limit=200&quot;,
        pokemon: [],
        itemsCountPerPage: 20,
        activePage: 1
      };
    
      loadPokemon = () =&gt; {
        axios
          .get(this.state.url)
          .then(res =&gt; {
            this.setState(prevState =&gt; {
              return {
                pokemon: [...prevState.pokemon, ...res.data.results],
                url: res.data.next
              };
            });
          })
          .catch(function(error) {
            // handle error
            console.log(error);
          });
      };
    
      render() {
        // console.log(this.state.pokemon);
        return (
          &lt;React.Fragment&gt;
            {this.state.pokemon ? (
              &lt;div className=&quot;row&quot;&gt;
                &lt;InfiniteScroll
                  pageStart={0}
                  loadMore={this.loadPokemon}
                  hasMore={this.state.url}
                  loader={
                    &lt;div className=&quot;loader&quot; key={0}&gt;
                      Loading ...
                    &lt;/div&gt;
                  }
                &gt;
                  {this.state.pokemon.map((pokemon, i) =&gt; (
                    &lt;div
                      style={{ borderBottom: &quot;1px solid&quot;, padding: &quot;10px&quot; }}
                      key={pokemon.name + i}
                    &gt;
                      &lt;div&gt;{pokemon.name}&lt;/div&gt;
                      &lt;div&gt;{pokemon.url}&lt;/div&gt;
                    &lt;/div&gt;
                  ))}
                &lt;/InfiniteScroll&gt;
              &lt;/div&gt;
            ) : (
              &lt;h1&gt;Loading Pokemon&lt;/h1&gt;
            )}
          &lt;/React.Fragment&gt;
        );
      }
    }
    
    const rootElement = document.getElementById(&quot;root&quot;);
    ReactDOM.render(&lt;App /&gt;, rootElement);



